/*Table structure for table `assessments` */



DROP TABLE IF EXISTS `assessments`;



CREATE TABLE `assessments` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `owner_id` int(11) DEFAULT NULL COMMENT 'User ID who created the assessment',
  `requester_id` int(11) DEFAULT NULL COMMENT 'User ID for which the assessment would be done',
  `case_number` varchar(100) DEFAULT NULL,
  `atype` enum('Self','Independent') DEFAULT NULL,
  `sub_type` enum('Generalized','Regulated','Other','FFIEC Regulated') DEFAULT NULL,
  `name` varchar(255) DEFAULT NULL,
  `status` enum('In Progress','Review or Draft','Rejected Pending Updates','Accepted','Completed') DEFAULT 'In Progress',
  `evidence_file` varchar(400) DEFAULT NULL,
  `file_name` varchar(255) DEFAULT NULL,
  `file_description` varchar(255) DEFAULT NULL,
  `signature` varchar(400) DEFAULT NULL,
  `result_status` enum('Pending','Published') DEFAULT 'Pending',
  `created` datetime DEFAULT NULL,
  `modified` datetime DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=109 DEFAULT CHARSET=latin1;



