/*Table structure for table `ffiec_maturity_levels` */



DROP TABLE IF EXISTS `ffiec_maturity_levels`;



CREATE TABLE `ffiec_maturity_levels` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(200) DEFAULT NULL,
  `description` text,
  `created` datetime DEFAULT NULL,
  `modified` datetime DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=6 DEFAULT CHARSET=latin1;



/*Data for the table `ffiec_maturity_levels` */



insert  into `ffiec_maturity_levels`(`id`,`name`,`description`,`created`,`modified`) values (1,'Baseline\r\n','Baseline maturity is characterized by minimum expectations required by law and regulations or recommended in supervisory guidance. This level includes compliance-driven objectives. Management has reviewed and evaluated guidance.','2019-09-18 00:00:00','2019-09-18 00:00:00'),(2,'Evolving\r\n','Evolving maturity is characterized by additional formality of documented procedures and policies that are not already required. Risk-driven objectives are in place. Accountability for cybersecurity is formally assigned and broadened beyond protection of customer information to incorporate information assets and systems.','2019-09-18 00:00:00','2019-09-18 00:00:00'),(3,'Intermediate\r\n','Intermediate maturity is characterized by detailed, formal processes. Controls are validated and consistent. Risk-management practices and analysis are integrated into business strategies.','2019-09-18 00:00:00','2019-09-18 00:00:00'),(4,'Advanced\r\n','Advanced maturity is characterized by cybersecurity practices and analytics that are integrated across lines of business. Majority of risk-management processes are automated and include continuous process improvement. Accountability for risk decisions by frontline businesses is formally assigned.','2019-09-18 00:00:00','2019-09-18 00:00:00'),(5,'Innovative\r\n','Innovative maturity is characterized by driving innovation in people, processes, and technology for the institution and the industry to manage cyber risks. This may entail developing new controls, new tools, or creating new information-sharing groups. Real-time, predictive analytics are tied to automated responses.','2019-09-18 00:00:00','2019-09-18 00:00:00');



