insert  into `risks`(`id`,`name`,`description`,`created`,`modified`) values (1,'Business-IT Strategy','<p>Risk of misalignment, miscommunication, misinvestment, and lack of engagement between IT and the business regarding business direction and the associated IT implications, including project management, that result in focusing on tactical solutions, rather than on the core issue, strategic alignment.</p>\r\n','2019-06-03 08:43:50','2019-08-20 06:31:09'),(2,'Human Resource','<p>Risk of failing to achieve business-IT objectives due to inadequate staff competency and resource misalignment and misallocation.</p>\r\n','2019-06-03 08:58:44','2019-06-03 08:58:44'),(3,'Regulatory Compliance','<p>Risk of violations of, or nonconformance with, laws, rules, regulations, prescribed practices, internal policies, and procedures, or ethical standards. &nbsp;</p>\r\n','2019-06-03 08:59:10','2019-06-03 08:59:10'),(4,'Information Security','<p>Risk of failures in protecting information and information systems from unauthorized access, use, disclosure, disruption, modification, or destruction.</p>\r\n','2019-06-03 09:00:35','2019-06-03 09:00:35'),(5,'Physical Security','<p>Risk of failures in protecting data center facility and assets from unauthorized access, use, disruption, modification, or destruction.</p>\r\n','2019-06-03 09:06:34','2019-06-03 09:06:34'),(6,'Business Resiliency','<p>Risk of loss or disruption of business and IT operations due to inability to recover from external events, such as natural disasters, terrorist attacks, and other man-made events, as a result of ineffective technology contingency planning, preparation, testing, and/or executions.<br />\r\n&nbsp;</p>\r\n','2019-06-03 09:06:57','2019-06-03 09:06:57'),(7,'Change Management','<p>Risk of failed, unapproved or erroneous changes (intentional and unintentional) to technology environments that result in interruption, failure, or performance degradation.</p>\r\n','2019-06-03 09:07:26','2019-06-03 09:07:26'),(8,'Fraud Management','<p>Risk of misrepresentation, manipulation, and misappropriation of financial statements, business transactions, and assets (including cash assets, and physical, information or intellectual assets) by internal or external parties, misconduct by privileged employees and insider tradings.&nbsp;</p>\r\n','2019-06-03 09:07:43','2019-06-03 09:07:43'),(9,'Service Delivery','<p>Risk of failure to deliver agreed-upon IT service and support to customers due to flawed technology infrastructure, processes and operations, etc.</p>\r\n','2019-06-03 09:08:02','2019-06-03 09:08:02'),(10,'Third Party','<p>Risk of failed transactions, processes or compliance associated with vendors, service providers, alliances and other business partners.</p>\r\n','2019-06-03 09:08:26','2019-06-03 09:08:26'),(11,'Scalability Management','<p>Risk of failing to adapt to changing business needs to harness ever-changing processing power with a minimum of disruption.</p>\r\n','2019-06-03 09:08:53','2019-06-03 09:08:53');



